#!/bin/sh
# This script changes the inode_ratio in `/etc/mke2fs.conf` because noms keeps running out of inodes. 
sudo sed -i '0,/inode_ratio/{s/.*inode_ratio.*/\tinode_ratio = 2048 # fet: this was the old setting 16384/}' /etc/mke2fs.conf | tee /root/update-inode-ratio.log
